CREATE TABLE #PROJECT_VERSION(
	ID_PROJECT_VERSION INT PRIMARY KEY IDENTITY(1,1) ,
	ID_PROJECT INT NOT NULL,
	[VERSION_NAME] [varchar](255) NULL,
	[DATE_VERSION_STR] [varchar](255) NOT NULL,
	[DATE_VERSION] DATETIME  NULL
)



--https://wordpress.org/news/category/releases/




SELECT  ID_PROJECT,VERSION_NAME,DATE_VERSION_STR, CONVERT(datetime,DATE_VERSION_STR) AS DATE_VERSION FROM #PROJECT_VERSION
ORDER BY ID_PROJECT_VERSION ASC



SELECT ISNULL(A.DATE_VERSION,cast(-53690 as datetime)) AS VERSION_START_DATE,
B.DATE_VERSION VERSION_END_DATE, B.ID_PROJECT,B.ID_PROJECT_VERSION,B.VERSION_NAME
INTO #VERSION_PERIOD
FROM PROJECT_VERSION A RIGHT JOIN PROJECT_VERSION B ON A.ID_PROJECT_VERSION = B.ID_PROJECT_VERSION - 1
ORDER BY 4 ASC




SELECT DISTINCT ID_PROJECT_VERSION,D.ID_COMMIT 
FROM DM_COMMITS D 
JOIN #VERSION_PERIOD P ON CONVERT(DATETIME, D.COMMIT_DATE,103) BETWEEN P.VERSION_START_DATE AND P.VERSION_END_DATE
ORDER BY ID_COMMIT


